import { useState, useRef, useEffect } from "react";
import { Button } from "./ui/button";
import { Card, CardContent } from "./ui/card";
import { Mic, Square } from "lucide-react";
import "./VoiceRecorder.css";

export default function VoiceRecorder() {
  const [isRecording, setIsRecording] = useState(false);
  const [logs, setLogs] = useState([]);
  const [history, setHistory] = useState([]);
  const mediaRecorderRef = useRef(null);
  const audioStreamRef = useRef(null);
  const websocketRef = useRef(null);

  // Загружаем историю с сервера
  const fetchHistory = async () => {
    try {
      const res = await fetch("http://localhost:8000/history");
      const data = await res.json();
      setHistory(data);
    } catch (err) {
      console.error("Error fetching history:", err);
    }
  };

  useEffect(() => {
    fetchHistory();
    return () => {
      if (websocketRef.current) {
        websocketRef.current.close();
      }
      if (audioStreamRef.current) {
        audioStreamRef.current.getTracks().forEach((track) => track.stop());
      }
    };
  }, []);

  // Запускаем запись с микрофона и открываем WebSocket-соединение
  const startRecording = async () => {
    try {
      audioStreamRef.current = await navigator.mediaDevices.getUserMedia({ audio: true });

      // Открытие WebSocket-соединения к серверу
      websocketRef.current = new WebSocket("ws://localhost:8000/ws");

      websocketRef.current.onopen = () => {
        console.log("WebSocket connected");
      };

      websocketRef.current.onmessage = (event) => {
        // Добавляем сообщение от сервера в логи
        setLogs((prevLogs) => [...prevLogs, event.data]);
        // Обновляем историю после получения сообщения
        fetchHistory();
      };

      websocketRef.current.onerror = (error) => {
        console.error("WebSocket error:", error);
      };

      mediaRecorderRef.current = new MediaRecorder(audioStreamRef.current);
      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0 && websocketRef.current.readyState === WebSocket.OPEN) {
          websocketRef.current.send(event.data);
        }
      };

      // Запускаем запись с интервалом формирования чанков (например, каждые 1000 мс)
      mediaRecorderRef.current.start(1000);
      setIsRecording(true);
    } catch (error) {
      console.error("Error accessing microphone:", error);
      alert("Пожалуйста, разрешите доступ к микрофону.");
    }
  };

  // Останавливаем запись и закрываем соединения
  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
    }
    if (audioStreamRef.current) {
      audioStreamRef.current.getTracks().forEach((track) => track.stop());
    }
    if (websocketRef.current) {
      websocketRef.current.close();
    }
    setIsRecording(false);
  };

  // Вызываем эндпоинт /clear для архивирования текущей сессии
  const clearData = async () => {
    try {
      const response = await fetch("http://localhost:8000/clear", { method: "POST" });
      if (response.ok) {
        setLogs([]);
        fetchHistory();
      } else {
        console.error("Ошибка при очистке данных");
      }
    } catch (err) {
      console.error("Error clearing data:", err);
    }
  };


  return (
    <Card className="card">
      <CardContent className="card-content">
        <h2>Voice Recorder</h2>
        <div className="recorder-container">
          <div className="recorder-circle">
            <div className={`recorder-circle-background ${isRecording ? "recording" : "idle"}`}></div>
            <Button
              variant={isRecording ? "destructive" : "default"}
              size="icon"
              className="recorder-button"
              onClick={isRecording ? stopRecording : startRecording}
            >
              {isRecording ? <Square className="h-6 w-6" /> : <Mic className="h-6 w-6" />}
            </Button>
          </div>
        </div>
        <div className={`recorder-status ${isRecording ? "recording" : "idle"}`}>
          {isRecording ? "Recording..." : "Press the microphone to start recording"}
        </div>
        <div style={{ marginTop: "20px" }}>
          <Button onClick={clearData}>Clear Data (Save to History)</Button>
        </div>
        <div style={{ marginTop: "20px" }}>
          <h3>Logs:</h3>
          <div style={{ border: "1px solid #ccc", padding: "10px", height: "150px", overflowY: "auto" }}>
            {logs.map((log, idx) => (
              <div key={idx}>{log}</div>
            ))}
          </div>
        </div>
        <div style={{ marginTop: "20px" }}>
          <h3>History (Last 5):</h3>
          <ul>
            {history.map((item, idx) => (
              <li key={idx}>{item}</li>
            ))}
          </ul>
        </div>
      </CardContent>
    </Card>
  );
}
