import { useState, useRef, useEffect } from "react";
import { Button } from "./ui/button";
import { Card, CardContent } from "./ui/card";
import { Mic, Square } from "lucide-react";
import "./VoiceRecorder.css";

export default function VoiceRecorder() {
  const [isRecording, setIsRecording] = useState(false);
  const [logs, setLogs] = useState([]);
  const [history, setHistory] = useState([]);
  const mediaRecorderRef = useRef(null);
  const audioStreamRef = useRef(null);
  const websocketRef = useRef(null);

  let ws;
let mediaRecorder;
let audioStream;

async function updateHistory() {
    const res = await fetch("/history");
    const history = await res.json();
    document.getElementById("historyList").innerHTML = history
        .map(file => `<div>${file}</div>`)
        .join("");
}

async function startRecording() {
    try {
        // Запрос доступа к микрофону
        audioStream = await navigator.mediaDevices.getUserMedia({ audio: true });
        
        document.getElementById("startBtn").disabled = true;
        document.getElementById("stopBtn").disabled = false;
        
        ws = new WebSocket("ws://" + window.location.host + "/ws");

        ws.onopen = () => {
            console.log("WebSocket connected");
        };

        ws.onmessage = (event) => {
            document.getElementById("output").innerHTML += event.data + "<br>";
            updateHistory();
        };

        ws.onerror = (error) => {
            console.error("WebSocket error:", error);
        };

        mediaRecorder = new MediaRecorder(audioStream);
        mediaRecorder.ondataavailable = (e) => {
            if (e.data.size > 0 && ws.readyState === WebSocket.OPEN) {
                // Отправляем бинарные данные на сервер
                ws.send(e.data);
            }
        };
        
        mediaRecorder.start(500);
    } catch (err) {
        alert("Разрешите доступ к микрофону!");
    }
}

function stopRecording() {
    // Останавливаем запись и отправляем специальное сообщение "STOP"
    if (mediaRecorder) {
        mediaRecorder.stop();
    }
    if (audioStream) {
        audioStream.getTracks().forEach(track => track.stop());
    }
    if (ws && ws.readyState === WebSocket.OPEN) {
        ws.send("STOP");
        ws.close();  // Закрываем WebSocket только один раз
    }
    document.getElementById("startBtn").disabled = false;
    document.getElementById("stopBtn").disabled = true;
}

async function clearHistory() {
    await fetch("/clear", { method: "POST" });
    updateHistory();
    document.getElementById("output").innerHTML = "";
}

// Загружаем историю при старте
updateHistory();


  return (
    <Card className="card">
      <CardContent className="card-content">
        <h2>Voice Recorder</h2>
        <div className="recorder-container">
          <div className="recorder-circle">
            <div className={`recorder-circle-background ${isRecording ? "recording" : "idle"}`}></div>
            <Button
              variant={isRecording ? "destructive" : "default"}
              size="icon"
              className="recorder-button"
              onClick={isRecording ? stopRecording : startRecording}
            >
              {isRecording ? <Square className="h-6 w-6" /> : <Mic className="h-6 w-6" />}
            </Button>
          </div>
        </div>
        <div className={`recorder-status ${isRecording ? "recording" : "idle"}`}>
          {isRecording ? "Recording..." : "Press the microphone to start recording"}
        </div>
        <div style={{ marginTop: "20px" }}>
          <Button onClick={clearData}>Clear Data (Save to History)</Button>
        </div>
        <div style={{ marginTop: "20px" }}>
          <h3>Logs:</h3>
          <div style={{ border: "1px solid #ccc", padding: "10px", height: "150px", overflowY: "auto" }}>
            {logs.map((log, idx) => (
              <div key={idx}>{log}</div>
            ))}
          </div>
        </div>
        <div style={{ marginTop: "20px" }}>
          <h3>History (Last 5):</h3>
          <ul>
            {history.map((item, idx) => (
              <li key={idx}>{item}</li>
            ))}
          </ul>
        </div>
      </CardContent>
    </Card>
  );
}
