import { useState, useRef, useEffect } from "react"
import { Button } from "./ui/button"
import { Card, CardContent } from "./ui/card"
import { Mic, Square, Play, Save, Trash2 } from "lucide-react"
import "./VoiceRecorder.css"

export default function VoiceRecorder() {
  const [isRecording, setIsRecording] = useState(false)
  const [recordings, setRecordings] = useState([])
  const [currentlyPlaying, setCurrentlyPlaying] = useState(null)
  const mediaRecorderRef = useRef(null)
  const audioChunksRef = useRef([])
  const audioElementRef = useRef(null)

  useEffect(() => {
    // Initialize audio element for playback
    audioElementRef.current = new Audio()
    audioElementRef.current.addEventListener("ended", () => {
      setCurrentlyPlaying(null)
    })

    return () => {
      // Clean up audio resources
      recordings.forEach((recording) => URL.revokeObjectURL(recording.url))
      if (audioElementRef.current) {
        audioElementRef.current.pause()
        audioElementRef.current.src = ""
      }
    }
  }, [])

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true })

      mediaRecorderRef.current = new MediaRecorder(stream)
      audioChunksRef.current = []

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data)
        }
      }

      mediaRecorderRef.current.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: "audio/wav" })
        const audioUrl = URL.createObjectURL(audioBlob)
        setRecordings((prev) => [...prev, { url: audioUrl, blob: audioBlob }])

        // Stop all tracks from the stream
        stream.getTracks().forEach((track) => track.stop())
      }

      mediaRecorderRef.current.start()
      setIsRecording(true)
    } catch (error) {
      console.error("Error accessing microphone:", error)
      alert("Could not access your microphone. Please check permissions.")
    }
  }

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop()
      setIsRecording(false)
    }
  }

  const playRecording = (index) => {
    if (audioElementRef.current) {
      // Stop current playback if any
      audioElementRef.current.pause()

      // Play the selected recording
      audioElementRef.current.src = recordings[index].url
      audioElementRef.current.play()
      setCurrentlyPlaying(index)
    }
  }

  const deleteRecording = (index) => {
    // Stop playback if this recording is playing
    if (currentlyPlaying === index && audioElementRef.current) {
      audioElementRef.current.pause()
      setCurrentlyPlaying(null)
    }

    // Revoke the URL to prevent memory leaks
    URL.revokeObjectURL(recordings[index].url)

    // Remove the recording from the array
    setRecordings(recordings.filter((_, i) => i !== index))
  }

  const downloadRecording = (index) => {
    const { url, blob } = recordings[index]
    const a = document.createElement("a")
    a.href = url
    a.download = `recording-${new Date().toISOString()}.wav`
    document.body.appendChild(a)
    a.click()
    document.body.removeChild(a)
  }

  return (
    <Card className="card">
      <CardContent className="card-content">
        <div className="recorder-container">
          <div className="recorder-circle">
            <div
              className={`recorder-circle-background ${isRecording ? "recording" : "idle"}`}
            ></div>
            <Button
              variant={isRecording ? "destructive" : "default"}
              size="icon"
              className="recorder-button"
              onClick={isRecording ? stopRecording : startRecording}
            >
              {isRecording ? <Square className="h-6 w-6" /> : <Mic className="h-6 w-6" />}
            </Button>
          </div>
        </div>

        <div className={`recorder-status ${isRecording ? "recording" : "idle"}`}>
          {isRecording ? "Recording..." : "Press the microphone to start recording"}
        </div>

        {recordings.length > 0 && (
          <div className="recordings-list">
            <h3 className="font-medium mb-3">Your Recordings</h3>
            <div className="space-y-3">
              {recordings.map((recording, index) => (
                <div key={index} className="recording-item">
                  <div className="font-medium">Recording {index + 1}</div>
                  <div className="recording-item-buttons">
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => playRecording(index)}
                      disabled={currentlyPlaying === index}
                    >
                      <Play className="h-4 w-4" />
                    </Button>
                    <Button variant="outline" size="icon" onClick={() => downloadRecording(index)}>
                      <Save className="h-4 w-4" />
                    </Button>
                    <Button variant="outline" size="icon" onClick={() => deleteRecording(index)}>
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  )
}
